import Hero from "../components/Hero";
import Navbar from "../components/Navbar";
import AboutImg from "../assets/2.jpg";
import Footer from "../components/Footer";
import ContactForm from "../components/ContactForm";
import { useState} from "react"
import axios from "axios"

function Contact() {
  const [email, setEmail] = useState('')
  const [message, setMessage] = useState('')
  const [error, setError] = useState('')
  const [selectValue] = useState('')

  const axiosPostData = async() => {
      const postData = {
          email: email,
          website: selectValue,
          message: message
      }

      await axios.post('http://localhost:1200/contact/send', postData)
      .then(res => setError(<p className="success">{res.data}</p>))
  }

  const handleSubmit = (e) => {
      e.preventDefault()

      if (!message) {
          setError(<p className="required">Message is empty. Please type a message.</p>)
      } else {
          setError('')
          axiosPostData()
      }
  }

  return (
      <>
          <Navbar />
          <Hero
            cName="hero-mid"
            heroImg={AboutImg}
            title="Contact"
            btnClass="hide"
          />
          <ContactForm />

          <form className="contactForm">
              <label>Email</label>
              <input type="text" id="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} />

              <label>Message</label>
              <textarea id="message" name="message" value={message} onChange={(e) => setMessage(e.target.value)}></textarea>
              
              {error}

              <button type="submit" onClick={handleSubmit}>Submit</button>
          </form>
          <Footer />
      </>

      
  )
}

export default Contact
